import { createTheme } from '@mui/material/styles'

const theme = createTheme({
  spacing: 4,
  palette: {
    primary: {
      main: '#E10856',
      light: '#1D1D1D',
      contrastText: '#FFFFFF',
    },
    text: {
      primary: '#AEAEB2',
      secondary: '',
      disabled: '',
    },
  },

  typography: {
    fontFamily: 'Roboto',
    caption: {
      fontSize: '14px',
      fontWeight: 500,
      lineHeight: '16px',
      textTransform: 'none',
      fontFamily: 'Roboto',
    },
    body1: {
      fontSize: '16px',
      fontWeight: 500,
      lineHeight: '22px',
      textTransform: 'none',
      fontFamily: 'Graphik',
    },
    body2: {
      fontSize: '16px',
      fontWeight: 400,
      lineHeight: '22px',
      textTransform: 'none',
      fontFamily: 'Graphik',
    },
    h1: {
      fontSize: '56px',
      fontWeight: 500,
      lineHeight: '64px',
      //textTransform: 'none',
      fontFamily: 'Roboto',
      fontStyle: 'normal'
    },
    h2: {
      fontSize: '24px',
      fontWeight: 400,
      lineHeight: '34px',
      textTransform: 'none',
      fontFamily: 'Graphik',
    },
    h3: {
      fontSize: '32px',
      fontWeight: 500,
      lineHeight: '40px',
      //textTransform: 'none',
      fontFamily: 'Roboto',
      fontStyle: 'normal'
    },
    h4: {
      fontSize: '18px',
      fontWeight: 400,
      lineHeight: '24px',
      //textTransform: 'none',
      fontFamily: 'Roboto',
      fontStyle: 'normal'
    },
    h5: {
      fontSize: '18px',
      fontWeight: 500,
      lineHeight: '24px',
      //textTransform: 'none',
      fontFamily: 'Roboto',
      fontStyle: 'normal'
    },
    h6: {
      fontSize: '16px',
      fontWeight: 500,
      lineHeight: '24px',
      //textTransform: 'none',
      fontFamily: 'Roboto',
      fontStyle: 'normal'
    },
    subtitle1: {
      fontSize: '16px',
      fontWeight: 500,
      lineHeight: '24px',
      //textTransform: 'none',
      fontFamily: 'Roboto',
      fontStyle: 'normal'
    },
    subtitle2: {
      fontSize: '14px',
      fontWeight: 500,
      lineHeight: '16px',
      textTransform: 'none',
      fontFamily: 'Roboto',
      fontStyle: 'normal'
    },
    button: {
      fontSize: '14px',
      fontWeight: 500,
      lineHeight: '42px',
      textTransform: 'none',
      fontFamily: 'Graphik',
    },
    overline: {
      fontSize: '12px',
      fontWeight: 400,
      lineHeight: '14px',
      textTransform: 'none',
      fontFamily: 'Graphik',
    },
  },

  components: {
    MuiButton: {
      styleOverrides: {
        outlined: {
          background: '#FFFFFF',
          color: '#0052FF',
          '&:hover': {
            background: 'none',
          },
        },
        outlinedPrimary: {
          borderColor: '#0052FF',
          background: '#FFFFFF',
          '&:hover': {
            background: 'none',
          },
        },
      },
    },
  },
})

export default theme
