import React, { useState, useEffect } from "react";

interface Tile {
  url: string;
  x: number;
  y: number;
}

interface Props {
  width: number;
  height: number;
}

const ImageConstructor: React.FC<Props> = ({ width, height }) => {
  const [tiles, setTiles] = useState<Tile[]>([]);

  useEffect(() => {
    const fetchTiles = async () => {
      const response = await fetch(
        `https://www.rijksmuseum.nl/api/en/collection/SK-C-216/tiles?width=${width}&height=${height}&key=2esrTh6M`
      );
      const data = await response.json();
      setTiles(data.tiles);
    };

    fetchTiles();
  }, [width, height]);

  return (
    <div>
      {tiles && tiles.length > 0 ? (
        <img
          src={tiles[0].url}
          alt="Rijksmuseum collection"
          style={{ width, height }}
        />
      ) : (
        <p>`Loading... {tiles.length}`</p>
      )}
    </div>
  );
};

export default ImageConstructor;
