import { useState, useEffect } from 'react';

function ImageConstructor({ id, height, width }) {
  const [imageUrl, setImageUrl] = useState('');

  useEffect(() => {
    // Fetch the image tiles
    fetch(`https://www.rijksmuseum.nl/api/en/collection/${id}/tiles?&key=2esrTh6M`)
      .then(response => response.json())
      .then(data => {
        // Construct the image URL from the tile data
        const baseUrl = data.baseUri.replace('{size}', `${width},${height}`);
        const tilePath = data.levels[data.levels.length - 1].tiles[0];
        const imageUrl = `${baseUrl}/${tilePath}`;
        setImageUrl(imageUrl);
      })
      .catch(error => {
        console.error('Error fetching image tiles:', error);
      });
  }, [id, height, width]);

  if (!imageUrl) {
    return <div>Loading...</div>;
  }

  return (
    <img
      src={imageUrl}
      alt={`Artwork with ID ${id}`}
      width={width}
      height={height}
    />
  );
}

export default ImageConstructor;
