import React, { useState, useEffect } from "react";
import axios from "axios";
import { Grid, Typography } from "@mui/material";
import PageNumbers from "../../molecules/PageNumbers";

interface ArtObject {
  id: string;
  title: string;
  webImage: {
    url: string;
    height: number;
    width: number;
  };
}

function ArtGrid() {
  const [artData, setArtData] = useState<ArtObject[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 9;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `https://www.rijksmuseum.nl/api/en/collection?key=2esrTh6M&involvedMaker=Rembrandt+van+Rijn&p=${currentPage}&ps=${itemsPerPage}`
        );
        setArtData(response.data.artObjects);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, [currentPage]);

  const handlePageChange = (event: React.ChangeEvent<unknown>, value: number) => {
    setCurrentPage(value);
  };

  return (
    <>
      <Grid container spacing={2}>
        {artData.length > 0 ? (
          artData.map((artObject) => (
            <Grid item xs={4} key={artObject.id}>
              <img src={artObject.webImage.url} alt={artObject.title} />
              <Typography variant="h6" align="center">
                {artObject.title}
              </Typography>
            </Grid>
          ))
        ) : (
          <Typography variant="h5" align="center">
            Loading...
          </Typography>
        )}
      </Grid>
      <PageNumbers handlePageChange={handlePageChange} />
    </>
  );
}

export default ArtGrid;
