import React, { useEffect, useState } from 'react';

interface Level {
  name: string;
  width: number;
  height: number;
  tileCountX: number;
  tileCountY: number;
  baseUrl: string;
}

interface Props {
  objectNumber: string;
  apiKey: string;
}

const ImageConstructor = ({ objectNumber, apiKey }: Props) => {
  const [tiles, setTiles] = useState<Level[]>([]);
  const [selectedLevel, setSelectedLevel] = useState<Level | null>(null);
  const [imageWidth, setImageWidth] = useState<number>(0);
  const [imageHeight, setImageHeight] = useState<number>(0);
  const [imageTiles, setImageTiles] = useState<JSX.Element[]>([]);

  useEffect(() => {
    const fetchTiles = async () => {
      const response = await fetch(`https://www.rijksmuseum.nl/api/nl/collection/${objectNumber}/tiles?key=${apiKey}`);
      const data = await response.json();
      setTiles(data.levels);
    };
    fetchTiles();
  }, [objectNumber, apiKey]);

  const selectLevel = (level: Level) => {
    setSelectedLevel(level);
    setImageWidth(level.width);
    setImageHeight(level.height);
    setImageTiles([]);
  };

  const constructImage = () => {
    if (!selectedLevel) return;
    const tiles = [];
    for (let y = 0; y < selectedLevel.tileCountY; y++) {
      for (let x = 0; x < selectedLevel.tileCountX; x++) {
        tiles.push(
          <img
            key={`${selectedLevel.name}-${x}-${y}`}
            src={`https://www.rijksmuseum.nl${selectedLevel.baseUrl.replace('{size}', '256')}/${x},${y},256,256/0/default.jpg`}
            alt=""
            style={{
              position: 'absolute',
              left: x * 256,
              top: y * 256,
              width: 256,
              height: 256,
            }}
          />
        );
      }
    }
    setImageTiles(tiles);
  };

  return (
    <div>
      <h1>Image Constructor</h1>
      <div>
        <h2>Select level</h2>
        {tiles.map((level: Level) => (
          <button key={level.name} onClick={() => selectLevel(level)}>
            {level.name} ({level.width}x{level.height})
          </button>
        ))}
      </div>
      {selectedLevel && (
        <div>
          <h2>Construct image</h2>
          <p>Width: {imageWidth}, Height: {imageHeight}</p>
          <button onClick={constructImage}>Construct</button>
          <div style={{ position: 'relative', width: imageWidth, height: imageHeight }}>
            {imageTiles}
          </div>
        </div>
      )}
    </div>
  );
};

export default ImageConstructor;
