import React from "react";
import { Box, Typography, styled, useMediaQuery } from "@mui/material";
import ImageConstructor from "../../atom/imageConstrtuctor";
import theme from "../../../theme/theme";

interface IImageCardDetail {
  objectNumber: string;
  longTitle: string;
}
const RootBox = styled(Box)({
  borderRadius: "8px",
  position: "relative",
  maxWidth: "1408px",
  height: "850px",
});

const TextBox = styled(Box)({
  position: "absolute",
  width: "1043px",
  left: "33px",
  bottom: "28px",
});

const ImageBox = styled(Box)({});

const ImageCardDetail = ({ objectNumber, longTitle }: IImageCardDetail) => {
  const isLargeScreen = useMediaQuery("(min-width:1400px)");
  console.log("is large screen: ", isLargeScreen);
  const width = 1408;
  const height = 850;

  return (
    <RootBox data-testid="molecule-CardDetail">
      <ImageBox sx={{ display: "flex" }}>
        <ImageConstructor
          width={isLargeScreen ? width : width / 2}
          height={isLargeScreen ? height : height / 2}
          id={objectNumber}
        />
      </ImageBox>
      <TextBox>
        <Typography
          variant="h1"
          color={theme.palette.primary.contrastText}
          children={longTitle}
        />
      </TextBox>
    </RootBox>
  );
};

export default ImageCardDetail;
