import { useState, useEffect, useRef } from 'react';
import axios from 'axios';

interface ITile {
  url: string;
  x: number;
  y: number;
}

interface IDataLevel {
  tiles: ITile[];
  width: number;
  height: number;
}

interface IData {
  levels: IDataLevel[];
}

interface IImageConstructorProps {
  width: number;
  height: number;
  id: string;
}

const ImageConstructor: React.FC<IImageConstructorProps> = ({ width, height, id }) => {
  const [tiles, setTiles] = useState<ITile[]>([]);

  useEffect(() => {
    const fetchTiles = async () => {
      try {
        const response = await axios.get(
          `https://www.rijksmuseum.nl/api/en/collection/${id}/tiles?&key=2esrTh6M`
        );
        const data: IData = response.data;
        let res = width * height;
        let closestResolution: number = 0;
        for(let i=0; i< data.levels.length; i++) {
          const distance = Math.sqrt((data.levels[i].width - width) ** 2 + (data.levels[i].height - height) ** 2);
          if (distance < res) {
              closestResolution = i;
              res = distance;
            }
        }
        console.log("resposne data: ",data.levels[closestResolution]);

        setTiles(data.levels[closestResolution].tiles);
      } catch (error) {
        console.error(error);
      }
    };

    fetchTiles();
  }, [width, height, id]);

  const [loadedTiles, setLoadedTiles] = useState(0);
  const imageRef = useRef<HTMLImageElement>(null);

  useEffect(() => {
    const canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    const ctx = canvas.getContext('2d');
    console.log(loadedTiles, );

    if (ctx && loadedTiles === tiles.length) {
      tiles.forEach(tile => {
        const tileImg = new Image();
        tileImg.crossOrigin = 'anonymous';
        tileImg.src = tile.url;
        tileImg.onload = () => {
          ctx.drawImage(tileImg, tile.x * 256, tile.y * 256);
          setLoadedTiles(prevState => prevState + 1);
          if (loadedTiles === tiles.length) {
            const imageUrl = canvas.toDataURL();
            if (imageRef.current) {
              imageRef.current.src = imageUrl;
            }
          }
        };
        tileImg.onerror = () => {
          setLoadedTiles(prevState => prevState + 1);
        };
      });
    }
  }, [loadedTiles, tiles, width, height]);

  return (
    <div data-testid="imageConstructor">
      {tiles && tiles.length > 0 ? (
        <img
          src=""
          alt="Rijksmuseum collection"
          style={{ width, height }}
          ref={imageRef}
        />
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ImageConstructor;
