import { Box } from "@mui/material";
import axios from "axios";
import React, { useState, useEffect, useRef } from "react";

interface ITile {
  url: string;
  x: number;
  y: number;
}

interface IImageConstructorProps {
  width: number;
  height: number;
  id: string;
}

const ImageConstructor: React.FC<IImageConstructorProps> = ({ width, height, id }) => {
  const [tiles, setTiles] = useState<ITile[]>([]);
  const canvasRef = useRef<HTMLCanvasElement | null>(null);

  useEffect(() => {
    const fetchTiles = async () => {
      try {
        const response = await axios.get(`https://www.rijksmuseum.nl/api/en/collection/${id}/tiles?&key=2esrTh6M`);
        const data = response.data;
        let res = width * height;
        let closestResolution: number = 0;
        for (let i = 0; i < data.levels.length; i++) {
          const distance = Math.sqrt((data.levels[i].width - width) ** 2 + (data.levels[i].height - height) ** 2);
          if (distance < res) {
            closestResolution = i;
            res = distance;
          }
        }
        setTiles(data.levels[closestResolution].tiles);
      } catch (error) {
        console.error(error);
      }
    };

    fetchTiles();
  }, [width, height, id]);

  useEffect(() => {
    if (canvasRef.current && tiles.length > 0) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext("2d");
      if (ctx) {
        let loadedTiles = 0;
        const loadedImageTiles = () => {
          loadedTiles++;
          if (loadedTiles === tiles.length) {
            const imgData = ctx.getImageData(0, 0, width, height);
            ctx.putImageData(imgData, 0, 0);
          }
        };
        for (let i = 0; i < tiles.length; i++) {
          const tile = tiles[i];
          const img = new Image();
          img.crossOrigin = "Anonymous";
          img.onload = () => {
            ctx.drawImage(img, tile.x, tile.y);
            loadedImageTiles();
          };
          img.src = tile.url;
        }
      }
    }
  }, [tiles, width, height]);

  return (
    <Box data-testid="imageConstructorBox" sx={{ display: "flex"}}>
      {tiles && tiles.length > 0 ? (
        <canvas ref={canvasRef}  style={{ boxSizing: "border-box" }} />
      ) : (
        <p>Loading...</p>
      )}
    </Box>
  );
};

export default ImageConstructor;
