import { Box } from "@mui/material";
import axios from "axios";
import React, { useEffect, useRef, useState } from "react";

interface ITile {
  url: string;
  x: number;
  y: number;
}

interface IImageConstructorProps {
  width: number;
  height: number;
  id: string;
}

const ImageConstructor: React.FC<IImageConstructorProps> = ({
  width,
  height,
  id,
}) => {
  const [tiles, setTiles] = useState<ITile[]>([]);
  const [loadedTiles, setLoadedTiles] = useState(0);
  const imageRef = useRef<HTMLImageElement>(null);

  useEffect(() => {
    const fetchTiles = async () => {
      try {
        const response = await axios.get(
          `https://www.rijksmuseum.nl/api/en/collection/${id}/tiles?&key=2esrTh6M`
        );
        const data = response.data;
        let res = width * height;
        let closestResolution: number = 0;
        for (let i = 0; i < data.levels.length; i++) {
          const distance = Math.sqrt(
            (data.levels[i].width - width) ** 2 +
              (data.levels[i].height - height) ** 2
          );
          if (distance < res) {
            closestResolution = i;
            res = distance;
          }
        }
        setTiles(data.levels[closestResolution].tiles);
      } catch (error) {
        console.error(error);
      }
    };

    fetchTiles();
  }, [width, height, id]);

  useEffect(() => {
    const drawImage = () => {
      const canvas = document.createElement("canvas");
      canvas.width = width;
      canvas.height = height;
      const ctx = canvas.getContext("2d");
      if (ctx) {
        tiles.forEach((tile) => {
          const tileImg = new Image();
          tileImg.crossOrigin = "anonymous";
          tileImg.src = tile.url;
          tileImg.onload = () => {
            ctx.drawImage(tileImg, tile.x, tile.y);
            setLoadedTiles((prevState) => prevState + 1);
          };
          tileImg.onerror = () => {
            setLoadedTiles((prevState) => prevState + 1);
          };
        });
        if (loadedTiles === tiles.length && imageRef.current) {
          imageRef.current.src = canvas.toDataURL();
        }
      }
    };

    if (tiles.length > 0) {
      drawImage();
    }
  }, [loadedTiles, tiles, width, height]);

  return (
    <Box sx={{ display: "flex" }} data-testid="imageConstructor">
      {tiles && tiles.length > 0 ? (
        <img
          src=""
          alt="Rijksmuseum collection"
          style={{ width, height }}
          ref={imageRef}
        />
      ) : (
        <p>Loading...</p>
      )}
    </Box>
  );
};

export default ImageConstructor;
