import React, { useState, useEffect } from "react";

interface Tile {
  x: number;
  y: number;
  url: string;
}

interface Level {
  name: string;
  width: number;
  height: number;
  tiles: Tile[];
}

interface CollectionImageData {
  levels: Level[];
}

const API_URL = "https://www.rijksmuseum.nl/api/nl/collection/SK-C-5/tiles?key=[api-key]";

const MyComponent: React.FC = () => {
  const [imageData, setImageData] = useState<CollectionImageData | null>(null);

  useEffect(() => {
    async function fetchImageData() {
      const response = await fetch(API_URL);
      const data = await response.json();
      setImageData(data);
    }

    fetchImageData();
  }, []);

  if (!imageData) {
    return <div>Loading...</div>;
  }

  const level = imageData.levels[0];
  const tile = level.tiles[0];

  const imageStyle = {
    position: "relative",
    width: `${level.width}px`,
    height: `${level.height}px`,
  };

  const tileStyle = {
    position: "absolute",
    top: `${tile.y * 256}px`,
    left: `${tile.x * 256}px`,
    width: "256px",
    height: "256px",
  };

  return (
    <div style={imageStyle}>
      <img src={tile.url} alt="Rijksmuseum Collection" style={tileStyle} />
    </div>
  );
};

export default MyComponent;
