import axios from "axios";
import React, { useState, useEffect } from "react";
import ImageComp from "../components/atom/Image";

interface ITile {
  url: string;
  x: number;
  y: number;
  tileSize: number | string;
}

interface IImageConstructorProps {
  width: number;
  height: number;
  id: string;
}

const ImageConstructor: React.FC<IImageConstructorProps> = ({ width, height, id }) => {
  const [tiles, setTiles] = useState<ITile[]>([]);
  const [imageSrc, setImageSrc] = useState<string | null>(null);

  useEffect(() => {
    const fetchTiles = async () => {
      try {
        const response = await axios.get(
          `https://www.rijksmuseum.nl/api/en/collection/${id}/tiles?&key=2esrTh6M`
        );
        const data = response.data;
        let res = width * height;
        let closestResolution: number = 0;
        for(let i=0; i< data.levels.length; i++) {
          const distance = Math.sqrt((data.levels[i].width - width) ** 2 + (data.levels[i].height - height) ** 2);
          if (distance < res) {
            closestResolution = i;
            res = distance;
          }
        }
        console.log("data: ",data,data.levels[closestResolution]);
        setTiles(data.levels[closestResolution].tiles);
      } catch (error) {
        console.error(error);
      }
    };

    fetchTiles();
  }, [width, height, id]);

  useEffect(() => {
    if (tiles.length > 0) {
      const canvas = document.createElement('canvas');
      canvas.width = width;
      canvas.height = height;
      const ctx = canvas.getContext('2d');
      const img = new Image();
      img.crossOrigin = 'Anonymous';
      let loadedTileCount = 0;

      const drawTile = (tile: ITile) => {
        const imgTile = new Image();
        imgTile.crossOrigin = 'Anonymous';
        imgTile.onload = () => {
          ctx?.drawImage(
            imgTile,
            tile.x * data.tileSize,
            tile.y * data.tileSize,
            data.tileSize,
            data.tileSize
          );
          loadedTileCount++;
          if (loadedTileCount === tiles.length) {
            setImageSrc(canvas.toDataURL());
          }
        };
        imgTile.src = tile.url;
      };

      const data = tiles[0];
      for (let i = 0; i < tiles.length; i++) {
        drawTile(tiles[i]);
      }
    }
  }, [tiles, width, height]);

  return (
    <div>
      {imageSrc ? (
        <img
          src={imageSrc}
          alt="Rijksmuseum collection"
          style={{ width, height }}
        />
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ImageConstructor;
