import { Box } from "@mui/material";
import axios from "axios";
import React, { useState, useEffect, useRef } from "react";

interface ITile {
  width: number;
  height: number;
  url: string;
  x: number;
  y: number;
}

interface IImageConstructorProps {
  width: number;
  height: number;
  id: string;
}

const ImageConstructor: React.FC<IImageConstructorProps> = ({ width, height, id }) => {
  const [tiles, setTiles] = useState<ITile[]>([]);
  const canvasRef = useRef<HTMLCanvasElement>(null);
  const imgRef = useRef<HTMLImageElement>(null);

  useEffect(() => {
    const fetchTiles = async () => {
      try {
        const response = await axios.get(
          `https://www.rijksmuseum.nl/api/en/collection/${id}/tiles?&key=2esrTh6M`
        );
        const data = response.data;
        let res = width * height;
        let closestResolution: number = 0;
        for (let i = 0; i < data.levels.length; i++) {
          const distance = Math.sqrt(
            (data.levels[i].width - width) ** 2 + (data.levels[i].height - height) ** 2
          );
          if (distance < res) {
            closestResolution = i;
            res = distance;
          }
        }
        setTiles(data.levels[closestResolution].tiles);
      } catch (error) {
        console.error(error);
      }
    };

    fetchTiles();
  }, [width, height, id]);

  useEffect(() => {
    if (tiles.length > 0 && canvasRef.current && imgRef.current) {
      const canvas = canvasRef.current;
      const ctx = canvas.getContext("2d");
      const tileWidth = tiles[0].width;
      const tileHeight = tiles[0].height;
      canvas.width = width;
      canvas.height = height;

      for (const tile of tiles) {
        const img = new Image();
        img.onload = () => {
          if (ctx) {
            ctx.drawImage(
              img,
              tile.x * tileWidth,
              tile.y * tileHeight,
              tileWidth,
              tileHeight
            );
          }
        };
        img.src = tile.url;
      }
      imgRef.current.src = canvas.toDataURL();
    }
  }, [tiles, width, height]);

  return (
    <Box sx={{ display: "flex" }} data-testid="imageConstructor">
      {tiles && tiles.length > 0 ? (
        <img
          ref={imgRef}
          alt="Rijksmuseum collection"
          style={{ width, height }}
        />
      ) : (
        <p>Loading...</p>
      )}
      <canvas
        ref={canvasRef}
        style={{ display: "none" }}
        width={width}
        height={height}
      />
    </Box>
  );
};

export default ImageConstructor;
