import React, { useState, useEffect } from "react";
import axios from "axios";
import { Grid, Typography } from "@mui/material";
import ImageConstructor from "../../atom/imageConstructor";
import ImageCard from "../../molecules/ImageCard";
import PageNumbers from "../../molecules/PageNumbers";
import { Link } from "react-router-dom";

function ArtworkGrid({}) {
  const [artData, setArtData] = useState<any[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 9;

  useEffect(() => {
    const fetchData = async () => {
        try {
          const response = await axios.get(
            `https://www.rijksmuseum.nl/api/en/collection?key=2esrTh6M&involvedMaker=Rembrandt+van+Rijn&p=${currentPage}&ps=${itemsPerPage}`
          );
          setArtData(response.data.artObjects);
        } catch (error) {
          console.log(error);
        }
      };
      fetchData();
    }, [currentPage]);

    const handlePageChange = (event: React.ChangeEvent<unknown>, value: number) => {
        setCurrentPage(value);
      };


  return (
    <>
    <Grid container spacing={7}>
      {artData.length > 0 ? (
        artData.map((artObject: any) => (
          <Grid item xs={4} key={artObject.id}>
            <Link to={`/artwork/${artObject.objectNumber}`}>
            <ImageCard
                    rightTypo={artObject.principalOrFirstMaker}
                    leftTypo={artObject.title}
                    imageClick={() => {
                      history.push(`/artwork/${artObject.objectNumber}`);
                    }}
                    width={450} 
                    height={364} 
                    id={artObject.objectNumber} 
            />
            </Link>
          </Grid>
        ))
      ) : (
        <Typography variant="h5" align="center">
          Loading...
        </Typography>
      )}
    </Grid>
    <PageNumbers handlePageChange={handlePageChange} />
    </>
  );
}

export default ArtworkGrid;
