import axios from "axios";
import React, { useState, useEffect } from "react";

interface ITile {
  url: string;
  x: number;
  y: number;
  tileSize: any;
}

interface IImageConstructorProps {
  width: number;
  height: number;
  id: string;
}

const ImageConstructor: React.FC<IImageConstructorProps> = ({
  width,
  height,
  id,
}) => {
  const [tiles, setTiles] = useState<ITile[]>([]);
  const [tileSize, setTileSize] = useState<number | undefined>(undefined);

  useEffect(() => {
    const fetchTiles = async () => {
      try {
        const response = await axios.get(
          `https://www.rijksmuseum.nl/api/en/collection/${id}/tiles?&key=2esrTh6M`
        );
        const data = response.data;
        let res = width * height;
        let closestResolution: number = 0;
        for (let i = 0; i < data.levels.length; i++) {
          const distance = Math.sqrt(
            (data.levels[i].width - width) ** 2 +
              (data.levels[i].height - height) ** 2
          );
          if (distance < res) {
            closestResolution = i;
            res = distance;
          }
        }
        console.log("data: ", data, data.levels[closestResolution]);
        setTiles(data.levels[closestResolution].tiles);
        setTileSize(data.levels[closestResolution].tileSize);
      } catch (error) {
        console.error(error);
      }
    };

    fetchTiles();
  }, [width, height, id]);

  return (
    <div style={{ position: "relative", width, height }}>
      {tiles && tiles.length > 0 ? (
        tiles.map((tile) => (
          <img
            key={tile.url}
            src={tile.url}
            alt="Rijksmuseum collection"
            style={{
              position: "absolute",
              top: tile.y * tileSize,
              left: tile.x * tileSize,
              width: tileSize,
              height: tileSize,
            }}
          />
        ))
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ImageConstructor;
