import React, { useEffect, useState } from 'react'
import ImageConstructor from '../../../imageConstructor'
import TableContent from '../../molecules/TableContent';
import { Box } from '@mui/material';
import axios from 'axios';


interface IArtWorkDetailsProp {
    objectNumber: string;
}
const ArtWorkDetails = ({objectNumber}: IArtWorkDetailsProp) => {

    const [artObjectData, setArtObjectData] = useState<any>();

    useEffect(() => {
    const fetchData = async () => {
        try {
          const response = await axios.get(
            `https://www.rijksmuseum.nl/api/en/collection/${objectNumber}?key=2esrTh6M`
          );
          setArtObjectData(response);
          console.log(artObjectData);
        } catch (error) {
          console.log(error);
        }
      };
      fetchData();
    }, []);

  return (
    <>
    <Box>
    <ImageConstructor width={1400} height={1165} id={objectNumber}/>
    <TableContent heading={'Title'} content={artObjectData?.longTitle}/>
    <TableContent heading={'Artist'} content={artObjectData?.principalOrFirstMaker}/>
    <TableContent heading={'Object Type'} content={artObjectData?.objectTypes.join(",")}/>
    <TableContent heading={'Measurements'} content={artObjectData?.subTitle}/>
    <TableContent heading={'Description'} content={artObjectData?.plaqueDescriptionEnglish}/>
    </Box>
    </>
  )
}

export default ArtWorkDetails
