import { Box, Button, styled } from "@mui/material";
import React, { useState } from "react";
import Logotext from "../LogoText";
import SearchFieldComponent from "../../atom/SearchBar";
import ButtonComponent from "../../atom/Button";
import axios from "axios";

// interface INavBarProp {
//   handleSearchResult: () => {};
//   handleSearch: () => void;
// }

interface Artwork {
  title: string;
  principalOrFirstMaker: string;
  longTitle: string;
  webImage: {
    url: string;
    height: number;
    width: number;
  };
}

const RootBox = styled(Box)(({ theme }) => ({
  display: "flex",
  gap: "32px",
  padding: "32px",
  alignItems: "center",
  justifyContent: "space-between",
  background: "rgba(31, 31, 31, 0.4)",
  border: "1px solid #CA35F7",
  boxShadow: "0px 40px 40px rgba(0, 0, 0, 0.16)",
  backdropFilter: "blur(12px)" /* Note: backdrop-filter has minimal browser support */,
  borderRadius: "53px",
  //height: "112px"
}));

const SearchBox = styled(Box)(({ theme }) => ({
  display: "flex",
  alignItems: "center",
}));

const API_BASE_URL = "https://www.rijksmuseum.nl/api/en";

const Navbar = () => {
  const [query, setQuery] = useState("");
  const [searchResults, setSearchResults] = useState<Artwork[]>([]);

  const handleSearch = async () => {
    const response = await axios.get<Artwork[]>(
      `${API_BASE_URL}/collection?q=${query}`
    );
    const artworks = response.data;
    setSearchResults(artworks);
    handleSearchResult(artworks);
  };
  return (
    <RootBox data-testid="molecule-navbar">
      <Box>
        <Logotext />
      </Box>
      <SearchBox>
        <SearchFieldComponent handleSearchResult={handleSearchResult} />
        <ButtonComponent children={"Search"} handleSearch={handleSearch} />
      </SearchBox>
    </RootBox>
  );
};

export default Navbar;
