import { Box } from "@mui/material";
import React, { useCallback, useEffect, useState } from "react";
import Navbar from "../../components/molecules/NavBar";
import ArtworkGrid from "../../components/organisms/allArtwork";
import FooterArtApi from "../../components/molecules/FooterArtApi";
import ArtWorkText from "../../components/atom/AllArtWorkText";
import axios from "axios";
import BasicTemplate from "../../components/template";
import { log } from "console";

const HomePage = () => {
  const [artData, setArtData] = useState<any[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const [isSearch, setSearch] = useState(false);
  const [searchValue, setSearchValue] = useState<string>('');
  const [filteredDataLength, setFilteredDataLength] = useState(0);
  const itemsPerPage = 9;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `https://www.rijksmuseum.nl/api/en/collection?key=2esrTh6M&involvedMaker=Rembrandt+van+Rijn&p=${currentPage}&ps=${itemsPerPage}`
        );
        setArtData(response.data.artObjects);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, [currentPage]);

  const handlePageChange = (
    event: React.ChangeEvent<unknown>,
    value: number
  ) => {
    setCurrentPage(value);
  };

  const handleSearchResult = useCallback((event: any) => {
    setSearchValue(event.target.value);
    setSearch(false);
  },[]);

  const handleSearch = useCallback(() => {
    setSearch(true);
  //   axios.get('https://www.rijksmuseum.nl/api/en/collection?key=2esrTh6M&involvedMaker=Rembrandt+van+Rijn')
  // .then(response => {
  //   let searchCount = 0;
  //   // Filter out the object numbers
  //   const objectNumbers = response.data.artObjects.map((item: { objectNumber: any; }) => item.objectNumber);
  //   // Use the object numbers to get more information from the second API
  //   objectNumbers.forEach((objectNumber: any) => {
  //     axios.get(`https://www.rijksmuseum.nl/api/en/collection/${objectNumber}?key=2esrTh6M`)
  //       .then(response => {
  //         console.log(response.data.artObject.objectCollection[0]);
  //         // Filter out the object numbers with objectCollection as "painting"
  //         if (response.data.artObject.objectCollection[0] ===  {searchValue}) {
  //           //console.log("on search: " + response.data.artObject.objectNumber);
  //           searchCount ++;
  //         }
  //         setFilteredDataLength(searchCount);
  //       })
  //       .catch(error => {
  //         console.log(error);
  //       });
  //   });
  // })
  // .catch(error => {
  //   console.log(error);
  // });
  axios.get('https://www.rijksmuseum.nl/api/en/collection', {
      params: {
        key: '2esrTh6M',
        q: searchValue
      }
    })
      .then(response => {
        console.log("me ahe ",response.data.artObject);
        let count = 0;
        response.data.artObjects.forEach((item: { objectCollection: string; }) => {
          if (item.objectCollection === searchValue) {
            count++;
          }
        });
        setFilteredDataLength(count);
      })
      .catch(error => {
        console.log(error);
      });
  },[filteredDataLength, searchValue]);

  return (
    <Box>
      <BasicTemplate
        handleSearchResult={handleSearchResult}
        handleSearch={handleSearch}
        middleData={
          <Box>
            <Box>
              <ArtWorkText
                artWorkText={
                  isSearch ? `Found ${filteredDataLength} result for : ${searchValue}` : "All artwork"
                }
              />
            </Box>
            <ArtworkGrid
              artData={artData}
              handlePageChange={handlePageChange}
            />
          </Box>
        }
      />
    </Box>
  );
};

export default HomePage;
