import React, { useState, useEffect } from "react";

const ImageConstructor: React.FC = () => {
  const [imageTiles, setImageTiles] = useState<any[]>([]);
  const [image, setImage] = useState<string>("");

  useEffect(() => {
    const fetchImageTiles = async () => {
      const response = await fetch(
        "https://www.rijksmuseum.nl/api/en/collection/SK-C-216/tiles?width=11206&height=9320&key=2esrTh6M"
      );
      const data = await response.json();
      setImageTiles(data.tiles);
    };

    fetchImageTiles();
  }, []);

  useEffect(() => {
    if (imageTiles.length > 0) {
      const tileSize = imageTiles[0].width;
      const rows = imageTiles.reduce((acc: any, tile: any) => {
        const row = Math.floor(tile.y / tileSize);
        if (!acc[row]) {
          acc[row] = [];
        }
        acc[row].push(tile);
        return acc;
      }, []);

      const image = rows
        .map((row: any) =>
          row
            .sort((a: any, b: any) => a.x - b.x)
            .map((tile: any) => tile.url)
            .join("")
        )
        .join("");

      setImage(image);
    }
  }, [imageTiles]);

  return <img src={image} alt="Artwork" />;
};

export default ImageConstructor;
