import { Box, Card, Typography, styled } from "@mui/material";
import React from "react";
import ImageComp from "../../atom/Image";

const RootBox = styled(Box)({
  position: "relative",
  width: "450px",
  "&:hover": {
    backgroundColor: "#E10856",
    color: "white",
  },
});

const ImageBox = styled(Box)({});

const RightTypoBox = styled(Box)({
  position: "absolute",
  top: "0px",
  right: "0px",
  background: "rgba(29, 29, 29, 0.5)",
  borderRadius: "0px 8px",
  padding: "4px 8px"
});

const LeftBottomTypoBox = styled(Box)({
  position: "absolute",
  bottom: "40px",
  left: "24px",
});

interface IImageCardProps {
  imageSource: string;
  rightTypo: string;
  leftTypo: string;
  imageClick: () => {};
}
const ImageCard = ({
  imageSource,
  imageClick,
  leftTypo,
  rightTypo,
}: IImageCardProps) => {
  return (
    <RootBox data-testid="moelcule-imageCard" onClick={imageClick}>
      <ImageBox>
        <ImageComp src={imageSource} alt={"Image"} />
      </ImageBox>
      <RightTypoBox>
        <Typography variant="subtitle2" color="#0FEFFD" children={rightTypo} />
      </RightTypoBox>
      <LeftBottomTypoBox>
        <Typography variant="h3" color="#C4C4C4" children={leftTypo} />
      </LeftBottomTypoBox>
    </RootBox>
  );
};

export default ImageCard;
