import React from "react";
import { Box, Typography, styled, useMediaQuery } from "@mui/material";
import ImageConstructor from "../../atom/imageConstrtuctor";
import theme from "../../../theme/theme";

interface IImageCardDetail {
  objectNumber: string;
  longTitle: string;
}

const RootBox = styled(Box)({
  borderRadius: "8px",
  position: "relative",
  maxWidth: "100%",
  height: "850px",
  paddingBottom: "56.25%", // Set aspect ratio to 16:9
  overflow: "hidden",
  '& img': {
    position: "absolute",
    width: "100%",
    height: "100%",
    objectFit: "cover",
    top: "0",
    left: "0",
  },
  '@media (min-width: 960px)': {
    height: "850px",
  },
});

const TextBox = styled(Box)({
  position: "absolute",
  width: "1043px",
  left: "33px",
  bottom: "28px",
  [theme.breakpoints.down("sm")]: {
    width: "700px",
  },
});

const ImageCardDetail = ({ objectNumber, longTitle }: IImageCardDetail) => {
  return (
    <RootBox data-testid="molecule-CardDetail">
      <ImageConstructor width={1408} height={850} id={objectNumber}/>
      <TextBox>
        <Typography
          variant="h1"
          color={theme.palette.primary.contrastText}
          children={longTitle}
        />
      </TextBox>
    </RootBox>
  );
};

export default ImageCardDetail;
