import { Box } from "@mui/material";
import axios from "axios";
import React, { useState, useEffect, useRef } from "react";

interface ITile {
  url: string;
  x: number;
  y: number;
}

interface IImageConstructorProps {
  width: number;
  height: number;
  id: string;
}

const ImageConstructor: React.FC<IImageConstructorProps> = ({ width, height, id }) => {
  const [tiles, setTiles] = useState<ITile[]>([]);
  const imageRef = useRef<HTMLImageElement>(null);

  useEffect(() => {
    const fetchTiles = async () => {
      try {
        const response = await axios.get(
          `https://www.rijksmuseum.nl/api/en/collection/${id}/tiles?&key=2esrTh6M`
        );
        const data = response.data;
        let res = width * height;
        let closestResolution: number = 0;
        for (let i = 0; i < data.levels.length; i++) {
          const distance = Math.sqrt((data.levels[i].width - width) ** 2 + (data.levels[i].height - height) ** 2);
          if (distance < res) {
            closestResolution = i;
            res = distance;
          }
        }
        console.log("tiles data: ",data.levels[closestResolution].tiles);
        setTiles(data.levels[closestResolution].tiles);
      } catch (error) {
        console.error(error);
      }
    };
    fetchTiles();
  }, [width, height, id]);

  useEffect(() => {
    const constructImage = async () => {
      if (tiles.length > 0) {
        const canvas = document.createElement("canvas");
        canvas.width = width;
        canvas.height = height;
        const ctx = canvas.getContext("2d")!;
        for (let i = 0; i < tiles.length; i++) {
          const img = new Image();
          img.src = tiles[i].url;
          const x = tiles[i].x;
          const y = tiles[i].y;
          await new Promise((resolve) => {
            img.onload = () => {
              ctx.drawImage(img, x, y);
              resolve(null);
            };
          });
        }
        console.log("current ref: ",canvas.toDataURL());
        imageRef.current!.src = canvas.toDataURL();

      }
    };
    constructImage();
  }, [width, height, tiles]);

  return (
    <Box sx={{ display: "flex" }} data-testid="imageConstructor">
      {tiles && tiles.length > 0 ? (
        <img
          ref={imageRef}
          alt="Rijksmuseum collection"
          style={{ width, height }}
        />
      ) : (
        <p>Loading...</p>
      )}
    </Box>
  );
};

export default ImageConstructor;
