import { Box } from '@mui/material'
import React, { useEffect, useState } from 'react'
import Navbar from '../../components/molecules/NavBar'
import ArtworkGrid from '../../components/organisms/allArtwork'
import FooterArtApi from '../../components/molecules/FooterArtApi'
import ArtWorkText from '../../components/atom/AllArtWorkText'
import axios from 'axios'

const HomePage = () => {

  const [artData, setArtData] = useState<any[]>([]);
  const [currentPage, setCurrentPage] = useState(1);
  const itemsPerPage = 9;

  useEffect(() => {
    const fetchData = async () => {
        try {
          const response = await axios.get(
            `https://www.rijksmuseum.nl/api/en/collection?key=2esrTh6M&involvedMaker=Rembrandt+van+Rijn&p=${currentPage}&ps=${itemsPerPage}`
          );
          setArtData(response.data.artObjects);
        } catch (error) {
          console.log(error);
        }
      };
      fetchData();
    }, [currentPage]);

  const handlePageChange = (event: React.ChangeEvent<unknown>, value: number) => {
    setCurrentPage(value);
  };
  return (
    <Box>
        {/* <Navbar handleSearchResult={function (): {} {
              throw new Error('Function not implemented.')
          } } handleSearch={function (): void {
              throw new Error('Function not implemented.')
          } }/> */}

        <ArtWorkText artWorkText={'All artwork'}/>
        <ArtworkGrid artData={undefined} handlePageChange={undefined}/>
        <FooterArtApi/>
    </Box>
  )
}

export default HomePage
