interface Tile {
    url: string;
    x: number;
    y: number;
  }

  async function getTiles(level: string): Promise<Tile[]> {
    const tilesResponse = await fetch(`https://www.rijksmuseum.nl/api/en/collection/SK-C-5/tiles/z${level}?key=2esrTh6M`);
    const tilesJson = await tilesResponse.json();
    const tiles: Tile[] = [];
    for (const tile of tilesJson.tiles) {
      tiles.push({
        url: tile.url,
        x: tile.x,
        y: tile.y
      });
    }
    return tiles;
  }

  export default async function getImage(width: number, height: number): Promise<HTMLImageElement> {
    const levelResponse = await fetch(`https://www.rijksmuseum.nl/api/en/collection/SK-C-5/tiles?width=${width}&height=${height}&key=2esrTh6M`);
    const levelJson = await levelResponse.json();
    const level = levelJson.levels[0];
    const tiles = await getTiles(level.name);
    const canvas = document.createElement('canvas');
    canvas.width = width;
    canvas.height = height;
    const ctx = canvas.getContext('2d');
    for (const tile of tiles) {
      const img = new Image();
      img.crossOrigin = 'anonymous';
      img.src = tile.url;
      await new Promise(resolve => img.onload = resolve);
      ctx && ctx.drawImage(img, tile.x, tile.y);
    }
    const fullImage = new Image();
    fullImage.src = canvas.toDataURL();
    return fullImage;
  }
