import axios from "axios";
import { log } from "console";
import React, { useState, useEffect } from "react";

interface ITile {
  url: string;
  x: number;
  y: number;
}

interface IImageConstructorProps {
  width: number;
  height: number;
  id: string;
}

const ImageConstructor: React.FC<IImageConstructorProps> = ({
  width,
  height,
  id,
}) => {
  const [tiles, setTiles] = useState<ITile[]>([]);
  const [tileSize, setTileSize] = useState<number>(0);

  useEffect(() => {
    const fetchTiles = async () => {
      try {
        const response = await axios.get(
          `https://www.rijksmuseum.nl/api/en/collection/${id}/tiles?&key=2esrTh6M`
        );
        const data = response.data;
        let res = width * height;
        let closestResolution: number = 0;
        for (let i = 0; i < data.levels.length; i++) {
          const distance = Math.sqrt(
            (data.levels[i].width - width) ** 2 +
              (data.levels[i].height - height) ** 2
          );
          if (distance < res) {
            closestResolution = i;
            res = distance;
          }
        }
        console.log("data: ", data, data.levels[closestResolution]);
        setTiles(data.levels[closestResolution].tiles);

        // Calculate tile size dynamically based on the number of tiles and image dimensions
        //const ImgWidth = data.levels[closestResolution].width;
        //const ImgHheight = data.levels[closestResolution].height;
        const tileSizeX = Math.ceil(width / Math.ceil(width / window.innerWidth));
        const tileSizeY = Math.ceil(height / Math.ceil(height / window.innerHeight));
        console.log(tileSizeX,tileSizeY);
        setTileSize(Math.min(tileSizeX, tileSizeY)/2);
        console.log(tileSize);
      } catch (error) {
        console.error(error);
      }
    };

    fetchTiles();
  }, [width, height, id]);

  const renderTiles = () => {
    const tileElements: JSX.Element[] = [];

    tiles.forEach((tile) => {
      const x = tile.x * tileSize;
      const y = tile.y * tileSize;

      const tileElement = (
        <img
          key={`${tile.url}-${x}-${y}`}
          src={tile.url}
          style={{
            position: "absolute",
            left: `${x}px`,
            top: `${y}px`,
            width: `${tileSize}px`,
            height: `${tileSize}px`,
          }}
        />
      );

      tileElements.push(tileElement);
    });

    return tileElements;
  };

  return (
    <div style={{ position: "relative", width, height }}>
      {tiles && tiles.length > 0 ? (
        renderTiles()
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ImageConstructor;
