import React, { useEffect, useState } from "react";

type Artwork = {
  objectNumber: string;
  title: string;
  webImage: {
    url: string;
  };
};

const API_KEY = "2esrTh6M";

const ArtworkGrid: React.FC = () => {
  const [artworks, setArtworks] = useState<Artwork[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetch(
        `https://www.rijksmuseum.nl/api/en/collection?key=${API_KEY}&involvedMaker=Rembrandt+van+Rijn`
      );
      const data = await response.json();
      setArtworks(data.artObjects);
    };
    fetchData();
  }, []);

  return (
    <div style={{ display: "grid", gridTemplateColumns: "repeat(3, 1fr)" }}>
      {artworks.slice(0, 9).map((artwork) => (
        <div key={artwork.objectNumber}>
          <img src={artwork.webImage.url} alt={artwork.title} />
        </div>
      ))}
    </div>
  );
};

export default ArtworkGrid;
