import axios from "axios";
import React, { useState, useEffect } from "react";

interface ITile {
  url: string;
  x: number;
  y: number;
  width: number;
  height: number;
}

interface IImageConstructorProps {
  width: number;
  height: number;
  id: string;
}

const ImageConstructor: React.FC<IImageConstructorProps> = ({ width, height, id }) => {
  const [tiles, setTiles] = useState<ITile[]>([]);
  const [tileSize, setTileSize] = useState<number>(512);

  useEffect(() => {
    const fetchTiles = async () => {
        try {
          const response = await axios.get(
            `https://www.rijksmuseum.nl/api/en/collection/${id}/tiles?&key=2esrTh6M`
          );
          const data = response.data;
          let res = width * height;
          let closestResolution: number = 0;
          for(let i=0; i< data.levels.length; i++) {
            const distance = Math.sqrt((data.levels[i].width - width) ** 2 + (data.levels[i].height - height) ** 2);
            if (distance < res) {
                closestResolution = i;
                res = distance;
              }
          }
          setTileSize(data.tileSize);
          setTiles(data.levels[closestResolution].tiles);
        } catch (error) {
          console.error(error);
        }
      };

    fetchTiles();
  }, [width, height, id]);

  return (
    <div>
      {tiles && tiles.length > 0 ? (
        <div style={{ width, height, position: 'relative' }}>
          {tiles.map((tile, index) => (
            <img
              key={index}
              src={tile.url}
              alt="Rijksmuseum collection"
              style={{ 
                position: 'absolute',
                left: tile.x / tileSize * width,
                top: tile.y / tileSize * height,
                width: tile.width / tileSize * width,
                height: tile.height / tileSize * height
              }}
            />
          ))}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ImageConstructor;
