import axios from "axios";
import React, { useState, useEffect } from "react";

interface ITile {
  url: string;
  x: number;
  y: number;
}

interface IImageConstructorProps {
  width: number;
  height: number;
  id: string;
}

const ImageConstructor: React.FC<IImageConstructorProps> = ({ width, height, id }) => {
  const [tiles, setTiles] = useState<ITile[]>([]);

  useEffect(() => {
    const fetchTiles = async () => {
      try {
        const response = await axios.get(
          `https://www.rijksmuseum.nl/api/en/collection/${id}/tiles?&key=2esrTh6M`
        );
        const data = response.data;
        let res = width * height;
        let closestResolution: number = 0;
        for (let i = 0; i < data.levels.length; i++) {
          const distance = Math.sqrt(
            (data.levels[i].width - width) ** 2 + (data.levels[i].height - height) ** 2
          );
          if (distance < res) {
            closestResolution = i;
            res = distance;
          }
        }
        const level = data.levels[closestResolution];
        const tileWidth = level.tileWidth;
        const tileHeight = level.tileHeight;
        const numCols = Math.ceil(level.width / tileWidth);
        const numRows = Math.ceil(level.height / tileHeight);
        const tiles: ITile[] = [];
        for (let row = 0; row < numRows; row++) {
          for (let col = 0; col < numCols; col++) {
            const x = col * tileWidth;
            const y = row * tileHeight;
            const url = `${level.baseUrl}/full/${x},${y}/${tileWidth},${tileHeight}/0/default.jpg`;
            tiles.push({ url, x, y });
          }
        }
        setTiles(tiles);
      } catch (error) {
        console.error(error);
      }
    };
    fetchTiles();
  }, [width, height, id]);

  return (
    <div>
      {tiles && tiles.length > 0 ? (
        <div
          style={{
            width: width,
            height: height,
            position: "relative",
            overflow: "hidden"
          }}
        >
          {tiles.map((tile, index) => (
            <img
              key={index}
              src={tile.url}
              alt="Rijksmuseum collection"
              style={{
                position: "absolute",
                top: tile.y,
                left: tile.x,
                width: width,
                height: height
              }}
            />
          ))}
        </div>
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ImageConstructor;
