import React, { useState, useEffect } from "react";
import axios from "axios";
import { Grid, Typography } from "@mui/material";
import ImageConstructor from "../../../imageConstructor";
import ImageCard from "../../molecules/ImageCard";

function ArtworkGrid() {
  const [artData, setArtData] = useState<any[]>([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          "https://www.rijksmuseum.nl/api/en/collection?key=2esrTh6M&involvedMaker=Rembrandt+van+Rijn"
        );
        setArtData(response.data.artObjects);
      } catch (error) {
        console.log(error);
      }
    };
    fetchData();
  }, []);

  return (
    <Grid container spacing={7}>
      {artData.length > 0 ? (
        artData.map((artObject: any) => (
          <Grid item xs={4} key={artObject.id}>
            <ImageConstructor
                  width={450}
                  height={364}
                  id={artObject.objectNumber}
                />
            <ImageCard
              imageSource={ undefined
                // <ImageConstructor
                //   width={450}
                //   height={364}
                //   id={artObject.objectNumber}
                // />
              }
              rightTypo={artObject.principalOrFirstMaker}
              leftTypo={artObject.title}
              imageClick={async () => window.alert("clicked biotch")}
            />
          </Grid>
        ))
      ) : (
        <Typography variant="h5" align="center">
          Loading...
        </Typography>
      )}
    </Grid>
  );
}

export default ArtworkGrid;
