import React, { useState, useEffect } from "react";

interface Tile {
  url: string;
  x: number;
  y: number;
}

interface Props {
  width: number;
  height: number;
}

const ImageConstructor: React.FC<Props> = ({ width, height }) => {
  const [tiles, setTiles] = useState<Tile[]>([]);

  useEffect(() => {
    const fetchTiles = async () => {
      const response = await fetch(
        `https://www.rijksmuseum.nl/api/en/collection/SK-C-216/tiles?&key=2esrTh6M`
      );
      //console.log(response);
      const data = await response.json();
      let res = width * height;
      let closestResolution: number = 0;
      for(let i=0; i< data.levels.length; i++) {
        const distance = Math.sqrt((data.levels[i].width - width) ** 2 + (data.levels[i].height - height) ** 2);
        if (distance < res) {
            closestResolution = i;
            res = distance;
          }
      }
      console.log("data: ",data.levels[closestResolution]);
      setTiles(data.levels[closestResolution].tiles);
      //console.log(data[0].tiles);

    };

    fetchTiles();
  }, [width, height]);

  return (
    <div>
      {tiles && tiles.length > 0 ? (
        <img
          src={tiles[0].url}
          alt="Rijksmuseum collection"
          style={{ width, height }}
        />
      ) : (
        <p>Loading...</p>
      )}
    </div>
  );
};

export default ImageConstructor;
